    node(int x) :val(x), left(NULL), right(NULL), father(NULL) {};
};
struct tree {
    node* root;
    tree() :root(NULL) {};

    //插入操作，成功插入返回1，否则返回0
    int insert(int x) {
        int kase = 0;
        //无根节点
        if (!root) { root = new node(x); return 1; }
        //有根节点
        node* p = root;
        node* q;
        while (p) {
            q = p;
            kase++;
            if (x val)p = p->left;
            else if (x > p->val)p = p->right;
            else return 0;
        }
        p = new node(x);
        p->father = q;
        if (x > q->val) q->right = p;
        else q->left = p;
        p->dep = kase;
        return 1;
    }

    //查询操作，有则返回指向这个节点的指针，没有则返回NULL
    node* find(int x) {
        node* p = root;
        while (p) {
            if (x val)p = p->left;
            else if (x > p->val)p = p->right;
            else return p;
        }
        return NULL;
    }

